CREATE FUNCTION public.check_credit_compatibility(p_project_id integer, p_target_jurisdiction character varying) RETURNS TABLE(project_name character varying, credit_type character varying, credit_name character varying, jurisdiction character varying, compliance_type character varying, compatibility_status character varying, available_markets character varying)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    SELECT 
        p.name::VARCHAR as project_name,
        p.credit_type::VARCHAR,
        ct.display_name::VARCHAR as credit_name,
        ct.jurisdiction::VARCHAR,
        ct.compliance_type::VARCHAR,
        CASE 
            WHEN ct.jurisdiction = p_target_jurisdiction OR ct.jurisdiction = 'International' THEN 'COMPATIBLE'::VARCHAR
            WHEN ct.compliance_type = 'both' THEN 'PARTIALLY_COMPATIBLE'::VARCHAR
            ELSE 'NOT_COMPATIBLE'::VARCHAR
        END as compatibility_status,
        tm.display_name::VARCHAR as available_markets
    FROM projects p
    JOIN credit_types ct ON p.credit_type = ct.credit_type_code
    LEFT JOIN target_markets tm ON tm.eligible_credit_types::text LIKE '%"' || p.credit_type || '"%'
    WHERE p.id = p_project_id
    AND (tm.jurisdiction = p_target_jurisdiction OR tm.jurisdiction = 'International' OR tm.market_type = 'voluntary');
END;
$$;



