CREATE VIEW public.applicable_requirements AS
 SELECT ec.id AS calculation_id,
    ec.location,
    ec.total_emissions,
    ec.industry_type,
    rr.requirement_name,
    rr.description,
    rr.threshold_emissions,
    rr.legislation_name,
    rr.legislation_url,
    rr.regulatory_body,
    rr.contact_email,
    rr.reporting_deadline,
    rr.legislation_summary,
    rr.effective_date,
        CASE
            WHEN ((rr.threshold_emissions IS NULL) OR (ec.total_emissions >= rr.threshold_emissions)) THEN true
            WHEN ((rr.industry_specific = true) AND ((ec.industry_type)::text = ANY (rr.applicable_industries))) THEN true
            ELSE false
        END AS is_applicable
   FROM (public.emissions_calculations ec
     CROSS JOIN public.reporting_requirements rr)
  WHERE (((ec.location)::text = (rr.country_code)::text) OR ((rr.country_code)::text = 'GLOBAL'::text));



