CREATE VIEW public.emissions_summary AS
 SELECT ec.id,
    ec.project_id,
    ec.reporting_year,
    ec.industry_type,
    ec.location,
    ec.scope1_total,
    ec.scope2_total,
    ec.scope3_total,
    ec.total_emissions,
    ec.reduction_target_percentage,
    ec.target_emissions,
    ec.verification_status,
    count(ers.id) AS strategy_count,
    ec.created_at,
    ec.updated_at
   FROM (public.emissions_calculations ec
     LEFT JOIN public.emission_reduction_strategies ers ON ((ec.id = ers.emissions_calculation_id)))
  GROUP BY ec.id, ec.project_id, ec.reporting_year, ec.industry_type, ec.location, ec.scope1_total, ec.scope2_total, ec.scope3_total, ec.total_emissions, ec.reduction_target_percentage, ec.target_emissions, ec.verification_status, ec.created_at, ec.updated_at;



