CREATE VIEW public.project_credit_summary AS
 SELECT p.id,
    p.name,
    p.category,
    p.credit_type,
    p.target_markets,
    p.location,
    ct.display_name AS credit_type_name,
    ct.jurisdiction AS credit_jurisdiction,
    ct.compliance_type,
    ct.registry_name
   FROM (public.projects p
     LEFT JOIN public.credit_types ct ON (((p.credit_type)::text = (ct.credit_type_code)::text)))
  WHERE ((p.project_type)::text = 'listing'::text);



--
--

CREATE VIEW public.project_pricing_summary AS
 SELECT p.id,
    p.name,
    p.category,
    p.credit_type,
    p.credit_price,
    p.credit_price_currency,
    p.credit_price_type,
    p.minimum_purchase,
    p.price_valid_until,
    p.reduction_target,
        CASE
            WHEN ((p.credit_price IS NOT NULL) AND (p.reduction_target IS NOT NULL)) THEN ((p.credit_price)::double precision * p.reduction_target)
            ELSE NULL::double precision
        END AS potential_revenue,
        CASE
            WHEN (p.price_valid_until < CURRENT_DATE) THEN 'expired'::text
            WHEN (p.price_valid_until <= (CURRENT_DATE + '7 days'::interval)) THEN 'expiring_soon'::text
            ELSE 'valid'::text
        END AS price_status
   FROM public.projects p
  WHERE ((p.project_type)::text = 'listing'::text);



