CREATE VIEW public.v_construction_products AS
 SELECT mp.id,
    mp.name,
    mp.company_name,
    mp.material_type,
    mp.emissions_reduction_factor,
    ((mp.integration_details -> 'construction'::text) ->> 'materialId'::text) AS material_id,
        CASE
            WHEN (((mp.integration_details -> 'construction'::text) ->> 'materialId'::text) ~~ '%standard'::text) THEN 'Standard'::text
            WHEN (((mp.integration_details -> 'construction'::text) ->> 'materialId'::text) ~~ '%recycled'::text) THEN 'Recycled'::text
            WHEN (((mp.integration_details -> 'construction'::text) ->> 'materialId'::text) ~~ '%green'::text) THEN 'Green/Low-Carbon'::text
            WHEN (((mp.integration_details -> 'construction'::text) ->> 'materialId'::text) ~~ '%bio'::text) THEN 'Bio-Based'::text
            ELSE 'Alternative'::text
        END AS material_variant
   FROM public.marketplace_products mp
  WHERE (mp.project_types ? 'construction'::text)
  ORDER BY mp.material_type, mp.emissions_reduction_factor DESC;



